//   Largest Time for Given Digits : https://leetcode.com/explore/challenge/card/september-leetcoding-challenge/554/week-1-september-1st-september-7th/3445/class Solution {public:    string largestTimeFromDigits(vector<int>& arr) {        sort(arr.begin(),arr.end());        vector<string> ans;        do {             string curr="";            curr+=arr[0]+'0';            curr+=arr[1]+'0';            curr+=":";            curr+=arr[2]+'0';            curr+=arr[3]+'0';            if(((arr[0]*10+arr[1])<24&&(arr[2]*10+arr[3]<60)))            {                                ans.push_back(curr);            }        } while (next_permutation(arr.begin(), arr.end()));         sort(ans.begin(),ans.end());        if(ans.size()==0)        {            if(arr[0]==0&&arr[1]==0&&arr[2]==0&&arr[3]==0)                return("00:00");            else return("");        }        else        {            string ret=ans[ans.size()-1];            return ret;        }    }};